
SELECT * FROM OPENQUERY(KAOQIN,'Select rdate,r.empno,r.empname,wkname,cast(isnull(gz_int,0)/60 as decimal(18,2)),cast(isnull(jb_ps_int,0)/60 as decimal(18,2)),cast(isnull(jb_xx_int,0)/60 as decimal(18,2)),cast(isnull(jb_jr_int,0)/60 as decimal(18,2)),ischeck,e.mjmark,e.dcmark,e.bcol1 from kq_dayreport r left join hr_employee e on r.empno = e.empno where (isnull(gz_int,0)> 0 or isnull(jb_ps_int,0)>0 or isnull(jb_xx_int,0) >0 or isnull(jb_jr_int,0)>0) and e.mjmark = ''制造中心'' and CONVERT(varchar(100), r.rdate, 20) like ''' + @cmonth + '%''')

